<?php/** * Created by PhpStorm. * User: Administrator * Date: 28.03.2018 * Time: 11:38 */namespace App\Queue;use Illuminate\Queue\Connectors\ConnectorInterface;use Bunny\Client;class RabbitMQConnector implements ConnectorInterface{    /**     * Establish a queue connection.     *     * @param  array $config     *     * @return \Illuminate\Contracts\Queue\Queue     */    public function connect(array $config)    {        $client = new Client([            "host" => $config['localhost'],            "port" => $config['5672'],            "vhost" => $config['vhost'],            "user" => $config['guest'],            "password" => $config['guest'],        ]);        return new RabbitMQQueue(            $client,            $config        );    }}