<?phprequire_once __DIR__ . '/../../../vendor/autoload.php';use PhpAmqpLib\Connection\AMQPStreamConnection;use PhpAmqpLib\Message\AMQPMessage;$connection = new AMQPStreamConnection('localhost', 5672, 'guest', 'guest');$channel = $connection->channel();$channel->queue_declare('task_queue', false, true, false, false);$data = implode(' ', array_slice($argv, 1));$jsonobj = '{	"firstname": "oliver",	"lng": "8° 32\' 12\" E",	"FK_pr_regions": 0,	"statement": "hi",	"family_members": 2,	"PK_pr_producer": "AB_00012",	"adress": "Feldstrasse 25",	"phone_number": "079 123 45 67",	"location": "zurich",	"lat": "47° 22\' 54\" N",	"lastname": "huber"}';if(empty($data)) $data = $jsonobj;$msg = new AMQPMessage($data,    array('delivery_mode' => AMQPMessage::DELIVERY_MODE_PERSISTENT));$channel->basic_publish($msg, '', 'task_queue');echo $data, "\n";$channel->close();$connection->close();